function EFFECT:Init(effectdata)  local pos = effectdata:GetOrigin()   sound.Play("weapons/physcannon/energy_disintegrate"..math.random(4, 5)..".wav", pos, 80, math.random(70, 90))  sound.Play("weapons/physcannon/energy_sing_explosion2.wav", pos, 80, math.random(105, 120))   local emitter = ParticleEmitter(pos)  emitter:SetNearClip(24, 32)   for i=1, 40 do  local particle = emitter:Add("effects/splash2", pos)  particle:SetDieTime(0.4)  particle:SetColor(255, 255, 255)  particle:SetStartAlpha(235)  particle:SetEndAlpha(0)  particle:SetStartSize(6)  particle:SetEndSize(6)  particle:SetStartLength(0)  particle:SetEndLength(90)  particle:SetVelocity(VectorRand():GetNormalized() * 90)  end   for i=1, 50 do  local heading = VectorRand():GetNormalized()   local particle = emitter:Add("effects/spark", pos + heading * 8)  particle:SetVelocity(250 * heading)  particle:SetDieTime(math.Rand(0.5, 0.85))  particle:SetStartAlpha(255)  particle:SetEndAlpha(255)  particle:SetStartSize(math.Rand(3, 4))  particle:SetEndSize(0)  particle:SetRoll(math.Rand(0, 360))  particle:SetRollDelta(math.Rand(-10, 10))  particle:SetAirResistance(20)  particle:SetStartLength(50)  particle:SetEndLength(0)  particle:SetColor(255, 255, 235)  end   for i=1,15 do  local particle = emitter:Add("sprites/glow04_noz", pos + VectorRand():GetNormalized() * 48)  particle:SetDieTime(math.Rand(0.3, 0.45))  particle:SetColor(255, 255, 240)  particle:SetStartAlpha(255)  particle:SetEndAlpha(0)  particle:SetStartSize(math.Rand(36, 48))  particle:SetEndSize(0)  particle:SetRoll(math.Rand(0, 360))  particle:SetRollDelta(math.Rand(-12, 12))  end   for i=1,7 do  local particle = emitter:Add("effects/fire_cloud2", pos)  particle:SetDieTime(math.Rand(0.3, 0.45))  particle:SetColor(210, 255, 255)  particle:SetStartAlpha(50)  particle:SetEndAlpha(0)  particle:SetStartSize(math.Rand(48, 64))  particle:SetEndSize(particle:GetStartSize() * 2)  particle:SetRoll(math.Rand(0, 360))  particle:SetRollDelta(math.Rand(-12, 12))  particle:SetVelocity(VectorRand():GetNormalized() * 120)  end   emitter:Finish() end  function EFFECT:Think()  return false end  function EFFECT:Render() end 