function EFFECT:Init(data)  local pos = data:GetOrigin()  local norm = data:GetNormal() * -1  sound.Play("physics/concrete/concrete_break"..math.random(2,3)..".wav", pos, 85, math.Rand(150, 155))   local random_props = {  "models/props_wasteland/rockcliff01f.mdl",  "models/props_wasteland/rockcliff01c.mdl",  "models/props_wasteland/rockgranite02c.mdl"  }   local maxbound = Vector(3, 3, 3)  local minbound = maxbound * -1  for i=1, math.random(4,6) do  local dir = VectorRand() * 5  dir.z = dir.z * 5  dir:Normalize()   local ent = ClientsideModel(table.Random(random_props), RENDERGROUP_OPAQUE)  if ent:IsValid() then  ent:SetModelScale(math.Rand(0.05, 0.1), 0)  ent:SetMaterial("models/props_wasteland/concretewall064b")  ent:SetColor(Color(60, 255, 60, 255))  ent:SetPos(pos + dir * 6)  ent:PhysicsInitBox(minbound, maxbound)  ent:SetCollisionBounds(minbound, maxbound)   local phys = ent:GetPhysicsObject()  if phys:IsValid() then  phys:SetMaterial("rock")  phys:ApplyForceCenter(dir * math.Rand(1700, 1800))  end   SafeRemoveEntityDelayed(ent, math.Rand(6, 10))  end  end end  function EFFECT:Think()  return false end  function EFFECT:Render() end 