function EFFECT:Think()  return false end  function EFFECT:Render() end  function EFFECT:Init(data)  local pos = data:GetOrigin()   local emitter = ParticleEmitter(pos)  emitter:SetNearClip(24, 32)  for i=1, 5 do  local particle = emitter:Add("effects/yellowflare", pos)  particle:SetDieTime(3)  particle:SetColor(230,200,210)  particle:SetStartAlpha(200)  particle:SetEndAlpha(0)  particle:SetStartSize(4)  particle:SetEndSize(0)  particle:SetRoll(math.Rand(0, 360))  particle:SetRollDelta(math.Rand(-60, 60))  particle:SetVelocity(VectorRand():GetNormal() * 30)  particle:SetAirResistance(10)  particle:SetGravity(Vector(0,0,30))  end   emitter:Finish() emitter = nil collectgarbage("step", 64) end 