function EFFECT:Init(data)  local pos = data:GetOrigin()   sound.Play(")weapons/explode"..math.random(3,5)..".wav", pos, 80, math.random(110, 120))   local emitter = ParticleEmitter(pos)  emitter:SetNearClip(32, 48)   for i=1, 12 do  local particle = emitter:Add("effects/fire_cloud1", pos)  particle:SetDieTime(0.2)  particle:SetColor(235,210,160)  particle:SetStartAlpha(255)  particle:SetEndAlpha(0)  particle:SetStartSize(0)  particle:SetEndSize(16)  particle:SetVelocity(VectorRand():GetNormalized() * 150)  end   for i=1, 7 do  local particle = emitter:Add("sprites/flamelet"..math.random(1, 4), pos)  particle:SetVelocity(VectorRand():GetNormalized() * 110)  particle:SetDieTime(0.2)  particle:SetStartAlpha(190)  particle:SetEndAlpha(0)  particle:SetStartSize(16)  particle:SetEndSize(1)  particle:SetRoll(math.Rand(0, 360))  particle:SetColor(255, 160, 120)  particle:SetRollDelta(math.Rand(-5, 5))  end   for i=1, 10 do  local particle = emitter:Add("effects/fire_embers"..math.random(1, 3), pos)  particle:SetVelocity(VectorRand():GetNormalized() * 250)  particle:SetDieTime(0.5)  particle:SetStartAlpha(130)  particle:SetEndAlpha(0)  particle:SetStartSize(math.Rand(5, 6))  particle:SetEndSize(1)  particle:SetRoll(math.Rand(0, 359))  particle:SetRollDelta(math.Rand(-3, 3))  particle:SetAirResistance(50)  particle:SetCollide(true)  particle:SetBounce(0.3)  particle:SetGravity(Vector(0,0,-400))  end   for i=1, 3 do  local particle = emitter:Add("particles/smokey", pos)  particle:SetVelocity(VectorRand():GetNormalized() * 16)  particle:SetDieTime(math.Rand(0.6, 0.7))  particle:SetLifeTime(particle:GetDieTime() * 0.6)  particle:SetStartAlpha(240)  particle:SetEndAlpha(0)  particle:SetColor(125, 105, 105)  particle:SetStartSize(math.Rand(35, 48))  particle:SetEndSize(particle:GetStartSize() + 12)  particle:SetRoll(math.Rand(0, 360))  particle:SetRollDelta(math.Rand(-5, 5))  particle:SetGravity(Vector(0, 0, 200))  end   emitter:Finish() emitter = nil collectgarbage("step", 64) end  function EFFECT:Think()  return false end  function EFFECT:Render() end 