function EFFECT:Think()  return false end  function EFFECT:Render() end  function EFFECT:Init(data)  local pos = data:GetOrigin()   sound.Play("physics/body/body_medium_break"..math.random(2, 4)..".wav", pos, 77, math.Rand(130, 140))   local emitter = ParticleEmitter(pos)  emitter:SetNearClip(16, 48)    local particle = emitter:Add("particles/smokey", pos)  particle:SetDieTime(0.2)  particle:SetStartAlpha(255)  particle:SetEndAlpha(0)  particle:SetStartSize(1)  particle:SetEndSize(24)  particle:SetRoll(math.Rand(0, 360))  particle:SetRollDelta(math.Rand(1, 2) * (math.random(2) == 1 and -1 or 1))  particle:SetColor(0, 150, 0)  particle:SetLighting(false)   for i = 1, 10 do  particle = emitter:Add("particles/smokey", pos)  particle:SetVelocity(VectorRand():GetNormalized() * math.Rand(60, 120))  particle:SetAirResistance(200)  particle:SetDieTime(math.Rand(0.4, 0.5))  particle:SetStartAlpha(240)  particle:SetEndAlpha(0)  particle:SetStartSize(math.Rand(1, 2))  particle:SetEndSize(0)  particle:SetRoll(math.Rand(0, 360))  particle:SetRollDelta(math.Rand(-30, 30))  particle:SetColor(0, 230, 0)  particle:SetLighting(false)  particle:SetGravity(Vector(0, 0, -200))  end   emitter:Finish() emitter = nil collectgarbage("step", 64) end 