function EFFECT:Think()  return false end  function EFFECT:Render() end  function EFFECT:Init(data)  local pos = data:GetOrigin()   sound.Play("physics/body/body_medium_break"..math.random(2, 4)..".wav", pos, 77, math.Rand(130, 140))   local emitter = ParticleEmitter(pos)  emitter:SetNearClip(16, 48)    for i = 1, math.random(100, 130) do  particle = emitter:Add("particles/smokey", pos)  particle:SetVelocity(VectorRand():GetNormalized() * math.Rand(128, 200))  particle:SetAirResistance(100)  particle:SetDieTime(math.Rand(0.9, 2))  particle:SetStartAlpha(230)  particle:SetEndAlpha(0)  particle:SetStartSize(math.Rand(1, 2))  particle:SetEndSize(0)  particle:SetRoll(math.Rand(0, 360))  particle:SetRollDelta(math.Rand(-30, 30))  particle:SetColor(0, 150, 0)  particle:SetLighting(false)  particle:SetGravity(Vector(0, 0, -400))  end   emitter:Finish() emitter = nil collectgarbage("step", 64) end 