EFFECT.LifeTime = 3  function EFFECT:Init(data)  self:SetRenderBounds(Vector(-64, -64, -64), Vector(64, 64, 64))   self.Seed = math.Rand(0, 10)  self.Pos = data:GetOrigin()   local status_index = math.Round(data:GetMagnitude())  local status_data  for _, v in pairs(GAMEMODE.StatusDisplays) do  if v.Index == status_index then  status_data = v  break  end  end   self.Message = string.upper(status_data.Name) .. "!"  self.Color   = status_data.Color   self.DeathTime = CurTime() + self.LifeTime end  function EFFECT:Think()  self.Pos.z = self.Pos.z + FrameTime() * 32  return CurTime() < self.DeathTime end  local cam_IgnoreZ = cam.IgnoreZ local cam_Start3D2D = cam.Start3D2D local cam_End3D2D = cam.End3D2D local draw_SimpleText = draw.SimpleText local math_Clamp = math.Clamp local math_sin = math.sin local EyeAngles = EyeAngles local TEXT_ALIGN_CENTER = TEXT_ALIGN_CENTER  function EFFECT:Render()  local delta = math_Clamp(self.DeathTime - CurTime(), 0, self.LifeTime) / self.LifeTime  self.Color.a = delta * 240   local ang = EyeAngles()  local right = ang:Right()  ang:RotateAroundAxis(ang:Up(), -90)  ang:RotateAroundAxis(ang:Forward(), 90)   cam_IgnoreZ(true)  cam_Start3D2D(self.Pos + math_sin(CurTime() + self.Seed) * 30 * delta * right, ang, (delta * 0.24 + 0.09) / 2)  draw_SimpleText(self.Message, "ZS3D2DFont2", 0, 0, self.Color, TEXT_ALIGN_CENTER, TEXT_ALIGN_CENTER)  cam_End3D2D()  cam_IgnoreZ(false) end 