function EFFECT:Think()  return false end  function EFFECT:Render() end  local matGlow = Material("sprites/light_glow02_add") function EFFECT:Init(data)  local pos = data:GetOrigin()   local emitter = ParticleEmitter(pos)  emitter:SetNearClip(24, 32)  local particle  for i=0, 8 do  particle = emitter:Add("effects/fire_cloud1", pos)  particle:SetVelocity(VectorRand() * 125)  particle:SetDieTime(0.3)  particle:SetStartAlpha(255)  particle:SetEndAlpha(0)  particle:SetStartSize(math.Rand(7, 9))  particle:SetEndSize(0)  particle:SetRoll(math.Rand(-0.8, 0.8))  particle:SetRollDelta(math.Rand(-3, 3))  particle:SetColor(245, 165, 30)  end  for i=0,2 do  particle = emitter:Add(matGlow, pos)  particle:SetVelocity(VectorRand() * 5)  particle:SetDieTime(0.2)  particle:SetStartAlpha(111)  particle:SetEndAlpha(0)  particle:SetStartSize(math.Rand(47, 59))  particle:SetEndSize(0)  particle:SetRoll(math.Rand(-0.8, 0.8))  particle:SetRollDelta(math.Rand(-3, 3))  particle:SetColor(245, 165, 60)  end  for i=1, 15 do  particle = emitter:Add("effects/splash2", pos)  particle:SetDieTime(0.5)  particle:SetColor(255, 110, 0)  particle:SetStartAlpha(255)  particle:SetEndAlpha(0)  particle:SetStartSize(5)  particle:SetEndSize(0)  particle:SetStartLength(1)  particle:SetEndLength(15)  particle:SetVelocity(VectorRand():GetNormal() * 90)  end  emitter:Finish() emitter = nil collectgarbage("step", 64) end 