local vec_up = vector_up function EFFECT:Init(data)  local pos = data:GetOrigin()  local norm = data:GetNormal() * -1  sound.Play("physics/glass/glass_largesheet_break"..math.random(1, 3)..".wav", pos, 65, math.Rand(110, 120))      local emitter = ParticleEmitter(pos, true)     emitter:SetNearClip(24, 32)      for i=1, math.random(25, 30) do         local particle = emitter:Add("effects/fleck_glass"..math.random(3), pos + VectorRand()*math.random( 3, 20 ))         particle:SetVelocity(VectorRand() * math.random(600,720))         particle:SetAirResistance(math.random(500,600))         particle:SetDieTime(math.Rand(1.1, 1.2))         particle:SetStartSize(math.Rand(3,5))         particle:SetEndSize( particle:GetStartSize() )         particle:SetStartAlpha(240)         particle:SetEndAlpha(0)         particle:SetRoll(math.Rand(0, 360))         particle:SetRollDelta(math.Rand(-760, -160))         particle:SetColor(60, 164, 255)         particle:SetGravity( vec_up * 100 )     end          for i=1, math.random(22, 33) do  particle = emitter:Add("effects/yellowflare", pos)  particle:SetVelocity(VectorRand():GetNormal() * math.random(500,620))  particle:SetAirResistance(math.random(500,600))  particle:SetGravity(vec_up * 100)  particle:SetDieTime(math.Rand(0.7, 1.3))  particle:SetStartAlpha(200)  particle:SetEndAlpha(0)  particle:SetStartSize(10)  particle:SetEndSize(0)  particle:SetRoll(math.Rand(0, 360))  particle:SetRollDelta(math.Rand(-15, 15))  particle:SetColor(55, 105, 255)  end   emitter:Finish() emitter = nil collectgarbage("step", 64) end  function EFFECT:Think()  return false end  function EFFECT:Render() end 