function EFFECT:Think()  return false end  function EFFECT:Render() end  function EFFECT:Init(data)  local pos = data:GetOrigin()   local emitter = ParticleEmitter(pos, true)  emitter:SetNearClip(28, 32)  local grav = Vector(0, 0, -300)  for i=1, math.random(4, 7) do  local particle = emitter:Add("effects/fleck_glass"..math.random(3), pos)  particle:SetVelocity((VectorRand() + Vector(0, 0, 1)):GetNormalized() * math.Rand(50, 64))  particle:SetDieTime(math.Rand(2, 2.5))  particle:SetStartAlpha(255)  particle:SetEndAlpha(255)  particle:SetStartSize(2)  particle:SetEndSize(0)  particle:SetRoll(math.Rand(0, 360))  particle:SetRollDelta(math.Rand(-750, 750))  particle:SetGravity(grav)  particle:SetColor(90, 35, 20)  particle:SetLighting(true)  end  emitter:Finish() emitter = nil collectgarbage("step", 64)   sound.Play("physics/wood/wood_plank_break"..math.random(1,4)..".wav", pos, 80, math.Rand(165, 170)) end 