function EFFECT:Init( data )  local pos = data:GetStart()   self.StartPos = pos  self.EndPos = data:GetOrigin()   self.Alpha = 255  self.Life = 0   local emitter = ParticleEmitter(pos)  emitter:SetNearClip(24, 32)   for i=1, 9 do  local particle = emitter:Add("effects/yellowflare", pos)  particle:SetDieTime(2)  particle:SetColor(230,200,210)  particle:SetStartAlpha(200)  particle:SetEndAlpha(0)  particle:SetStartSize(6)  particle:SetEndSize(0)  particle:SetRoll(math.Rand(0, 360))  particle:SetRollDelta(math.Rand(-10, 10))  particle:SetVelocity(VectorRand():GetNormal() * 800)  particle:SetAirResistance(300)  end   emitter:Finish() emitter = nil collectgarbage("step", 64)   self:SetRenderBoundsWS( self.StartPos, self.EndPos ) end  function EFFECT:Think( )  self.Life = self.Life + FrameTime() * 6  self.Alpha = 255 * ( 1 - self.Life )  return ( self.Life < 1 ) end  function EFFECT:Render()  if ( self.Alpha < 1 ) then return end end 