function EFFECT:Init( data )  local pos = data:GetStart()   self.StartPos = pos  self.EndPos = data:GetOrigin()   self.Alpha = 255  self.Life = 0   local emitter = ParticleEmitter(pos)  emitter:SetNearClip(24, 32)   for i=1, 9 do  local particle = emitter:Add("effects/blueflare1", pos)  particle:SetDieTime(0.8)  particle:SetColor(110,190,255)  particle:SetStartAlpha(200)  particle:SetEndAlpha(0)  particle:SetStartSize(4)  particle:SetEndSize(0)  particle:SetVelocity(VectorRand():GetNormal() * 50)  end   emitter:Finish() emitter = nil collectgarbage("step", 64)   self:SetRenderBoundsWS( self.StartPos, self.EndPos ) end  function EFFECT:Think( )  self.Life = self.Life + FrameTime() * 6  self.Alpha = 255 * ( 1 - self.Life )  return self.Life < 1 end  local beam1mat = Material("trails/electric") local glowmat = Material("sprites/light_glow02_add")  function EFFECT:Render()  if ( self.Alpha < 1 ) then return end   local texcoord = math.Rand( 0, 1 )  local norm = (self.StartPos - self.EndPos) * self.Life  self.Length = norm:Length()   render.SetMaterial( beam1mat )  for i = 1, 2 do  render.DrawBeam(self.StartPos + (VectorRand() * 0.3), self.EndPos + (VectorRand() * 6), 6, texcoord, texcoord + self.Length / 32, Color( 50, 235, 195, self.Alpha ))  end      local inv = 1 - self.Life   render.SetMaterial(glowmat)  render.DrawSprite(self.StartPos, 150 * inv, 150 * inv, Color(80, 155, 255, self.Alpha))  render.DrawSprite(self.EndPos, 150 * inv, 150 * inv, Color(80, 155, 255, self.Alpha)) end 