function EFFECT:Init(data)  local pos = data:GetOrigin()   sound.Play("physics/glass/glass_largesheet_break"..math.random(1, 3)..".wav", pos, 75, math.Rand(250, 255))   local emitter = ParticleEmitter(pos)  emitter:SetNearClip(8, 16)   local dir, size, brightness  local gravity = Vector(0, 0, -400)   for i=1, 10 do  dir = VectorRand()  dir:Normalize()  size = math.Rand(1, 5)  brightness = math.Rand(0.8, 1.0)   local particle = emitter:Add("particles/balloon_bit", pos + dir)  particle:SetVelocity(dir * math.Rand(48, 90))  particle:SetDieTime(math.Rand(3, 5))  particle:SetStartAlpha(255)  particle:SetEndAlpha(100)  particle:SetStartSize(size)  particle:SetEndSize(size / 4)  particle:SetRoll(math.Rand(0, 360))  particle:SetRollDelta(math.Rand(-2, 2))  particle:SetGravity(gravity)  particle:SetColor(140 * brightness, 170 * brightness, 255 * brightness)  particle:SetCollide(true)  particle:SetAngleVelocity(Angle(math.Rand(-160, 160), math.Rand(-160, 160), math.Rand(-160, 160)))  particle:SetBounce(0.9)  end   emitter:Finish() emitter = nil collectgarbage("step", 64) end  function EFFECT:Think()  return false end  function EFFECT:Render() end 