include("shared.lua")  function ENT:Initialize()  self:DrawShadow(false)  self:SetModelScale(2.13, 0)   self.AmbientSound = CreateSound(self, ")weapons/physcannon/superphys_hold_loop.wav")  self.AmbientSound:PlayEx(1, 35)   self:GetOwner().ShadeControl = self end  function ENT:OnRemove()  self.AmbientSound:Stop()   local owner = self:GetOwner()  if owner.ShadeControl == self then  owner.ShadeControl = nil  end end  local matRefract = Material("models/spawn_effect2") function ENT:Draw()  render.SetBlend(0.2)  render.SetColorModulation(0, 1, 0)  render.ModelMaterialOverride(matRefract)  self:DrawModel()  render.ModelMaterialOverride(0)  render.SetBlend(1)  render.SetColorModulation(1, 1, 1) end 