include("shared.lua")  ENT.NextEmit = 0  function ENT:Initialize()  self:SetColor(Color(0, 255, 0, 255))  self:SetModelScale(1, 0) end  local matGlow = Material("sprites/light_glow02_add") local matWhite = Material("models/debug/debugwhite") function ENT:Draw()  local real_pos = self:GetPos()   render.ModelMaterialOverride(matWhite)  render.SetColorModulation(0.2, 0.9, 0.2)  render.SuppressEngineLighting(true)  self:SetPos(real_pos + VectorRand() * 2)  self:DrawModel()  render.SuppressEngineLighting(false)  render.ModelMaterialOverride(nil)   render.SetMaterial(matGlow)  render.DrawSprite(self:GetPos(), 55, 55, Color(30, 255, 30))   self:SetPos(real_pos)   render.SetColorModulation(1, 1, 1) end 