include("shared.lua")  function ENT:Initialize()  self:DrawShadow(false)  self.Col = Color(90, 50, 110)    self.PlaceTime = CurTime() + self.SetTime  self.DeathTime = CurTime() + self.ExplodeTime    self:SetRenderBounds(Vector(-200, -200, -200), Vector(200, 200, 200))  self.AmbientSound = CreateSound(self, "ambient/levels/labs/machine_ring_resonance_loop1.wav") end  function ENT:Think()  local delta = math.Clamp((self.DeathTime - CurTime())/self.ExplodeTime, 0, 1)  self.AmbientSound:PlayEx(1, math.max(20, 130 * (1 - delta))) end  local matGlow = Material("effects/blueflare1") local dark = Color(80, 40, 100) function ENT:DrawTranslucent()  local delta = math.Clamp((self.DeathTime - CurTime())/self.ExplodeTime, 0, 1)  local col = Color(self.Col.r, self.Col.g, self.Col.b, 255)    render.SetMaterial(matGlow)   local pos = self:WorldSpaceCenter() + Vector(0, 0, 15) * math.sin(CurTime() * 3 * math.pi / (self.ExplodeTime - self.SetTime))  local size = 200 * (1 - delta)  render.DrawSprite(pos, size, size * 1.2, col)  render.DrawSprite(pos, size * 0.4, size, dark)  render.DrawSprite(pos, size * 0.1, size * 0.5, dark) end  function ENT:OnRemove()  self.AmbientSound:Stop() end 