include("shared.lua")  ENT.NextEmit = 0  ENT.RenderGroup = RENDERGROUP_TRANSLUCENT  function ENT:Initialize()  self:DrawShadow(false)  self:SetCustomCollisionCheck(true)  self:SetCollisionGroup(COLLISION_GROUP_PROJECTILE) end  local matGlow = Material("sprites/light_glow02_add") local matWhite = Material("models/debug/debugwhite") function ENT:Draw()  render.ModelMaterialOverride(matWhite)  render.SetColorModulation(0.8, 0.9, 1)  render.SuppressEngineLighting(true)     render.SetBlend(0.5)  self:DrawModel()     render.SetBlend(1)  render.SuppressEngineLighting(false)  render.ModelMaterialOverride(nil)   local pos = self:GetPos()   render.SetMaterial(matGlow)  render.DrawSprite(pos, 7, 7)   render.SetColorModulation(1, 1, 1) end 