include("shared.lua")  ENT.SmokeTimer = 0  function ENT:Initialize()  self:DrawShadow(false) end  function ENT:Think()  local pos = self:GetPos()  local emitter = ParticleEmitter(pos)  emitter:SetNearClip(24, 32)       if self.SmokeTimer < CurTime() then  self.SmokeTimer = CurTime() + 0.04   local particle  particle = emitter:Add("particles/smokey", pos + VectorRand() * 5)  particle:SetDieTime(math.Rand(0.4, 0.6))  particle:SetStartAlpha(math.Rand(70, 80))  particle:SetEndAlpha(0)  particle:SetStartSize(2)  particle:SetEndSize(math.Rand(4, 6))  particle:SetRoll(math.Rand(0, 359))  particle:SetRollDelta(math.Rand(-3, 3))  particle:SetColor(210, 220, 230)  end   emitter:Finish() emitter = nil collectgarbage("step", 64) end 