include("shared.lua")  ENT.NextEmit = 0  function ENT:Initialize()  self:SetMaterial("models/shadertest/shader2")  self:SetColor(Color(0, 150, 255, 255))  self:SetModelScale(0.3, 0) end  function ENT:Draw()  render.SuppressEngineLighting(true)  self:DrawModel()  render.SuppressEngineLighting(false)   if CurTime() < self.NextEmit then return end  self.NextEmit = CurTime() + 0.025   local pos = self:GetPos()   local emitter = ParticleEmitter(pos)  emitter:SetNearClip(24, 32)   local particle = emitter:Add("particles/smokey", pos)  particle:SetDieTime(math.Rand(0.5, 0.6))  particle:SetStartAlpha(255)  particle:SetEndAlpha(0)  particle:SetStartSize(math.Rand(3, 6))  particle:SetEndSize(0)  particle:SetRoll(math.Rand(0, 255))  particle:SetRollDelta(math.Rand(-10, 10))  particle:SetVelocity((self:GetVelocity():GetNormalized() * -1 + VectorRand():GetNormalized()):GetNormalized() * math.Rand(36, 48))  particle:SetLighting(true)  particle:SetColor(0, 150, 255, 255)   emitter:Finish() emitter = nil collectgarbage("step", 64) end 