include("shared.lua")  function ENT:Initialize()  self:SetMaterial("models/shadertest/shader2")  self:SetColor(Color(50, 190, 255, 255))  self:SetModelScale(0.07, 0) end  function ENT:DrawTranslucent()  render.SetBlend(0.65)  render.SuppressEngineLighting(true)  self:DrawModel()  render.SuppressEngineLighting(false)  render.SetBlend(1)      local next_pop = self:GetNextPop()     if next_pop ~= 0 then         local pos = self:GetPos()          local emitter = ParticleEmitter(pos)  emitter:SetNearClip(16, 24)          local pop_diff = 3 - math.max(0, self:GetNextPop() - CurTime())   for i = 1, 3 do  local particle = emitter:Add("effects/fire_embers"..math.random(1, 3), pos + VectorRand():GetNormalized() * pop_diff * 4)  particle:SetDieTime(math.Rand(0.1, 0.2) + pop_diff * 0.13)  particle:SetStartSize(1)  particle:SetEndSize(6 * pop_diff)  particle:SetStartAlpha(255)  particle:SetEndAlpha(0)  particle:SetVelocity(VectorRand() * pop_diff * 20)  particle:SetAirResistance(32)  particle:SetRoll(math.Rand(0, 360))  particle:SetStartLength(5)  particle:SetEndLength(15)  particle:SetColor(60, 130, 255)  particle:SetRollDelta(math.Rand(-1.5, 1.5))  end   emitter:Finish() emitter = nil collectgarbage("step", 64)     end end