include("shared.lua")  ENT.NextEmit = 0 local material = Material("Models/Barnacle/barnacle_sheet")  function ENT:Initialize()  self.AmbientSound = CreateSound(self, "ambient/gas/steam_loop1.wav")  self:DrawShadow(false)  self:SetColor(Color(30, 230, 30, 255))  self.Created = CurTime() end  function ENT:Think()  self.AmbientSound:PlayEx(0.4, 140 - math.min(2, CurTime() - self.Created) * 20)  self:NextThink(CurTime())  return true end  function ENT:OnRemove()  self.AmbientSound:Stop() end  function ENT:Draw()  render.ModelMaterialOverride(material)  render.SuppressEngineLighting(true)  self:DrawModel()  render.SuppressEngineLighting(false)  render.SetColorModulation(1, 1, 1)  render.ModelMaterialOverride(nil)   if CurTime() < self.NextEmit then return end  self.NextEmit = CurTime() + 0.025   local pos = self:GetPos()   local emitter = ParticleEmitter(pos)  emitter:SetNearClip(24, 32)      local particle = emitter:Add("particles/smokey", pos)     particle:SetVelocity(VectorRand():GetNormalized() * math.Rand(10, 40))     particle:SetDieTime(0.3)     particle:SetStartAlpha(230)     particle:SetEndAlpha(0)     particle:SetStartSize(1)     particle:SetEndSize(0)     particle:SetRoll(math.Rand(0, 360))     particle:SetRollDelta(math.Rand(-25, 25))     particle:SetGravity(Vector(0, 0, -100))     particle:SetColor(0, 150, 0)    emitter:Finish() emitter = nil collectgarbage("step", 64) end 