include("shared.lua")  function ENT:SetObjectHealth(health)  self:SetDTFloat(0, health) end  local colFlash = Color(30, 255, 30) function ENT:Draw()  self:DrawModel()   if not MySelf:IsValid() or MySelf:Team() ~= TEAM_HUMAN then return end     if EyePos():DistToSqr(self:GetPos()) >= 202500 then return end   local owner = self:GetObjectOwner()   local w, h = 600, 420   cam.Start3D2D(self:LocalToWorld(Vector(1, 0, self:OBBMaxs().z)), self:GetAngles(), 0.05)   if MySelf:Team() == TEAM_HUMAN then  colFlash.a = math.abs(math.sin(CurTime() * 3)) * 255  draw.SimpleText(translate.Get("purchase_now"), "ZS3D2DFont2", 0, -200, colFlash, TEXT_ALIGN_CENTER)  end  if owner:IsValid() and owner:IsPlayer() then  draw.SimpleText("("..owner:ClippedName()..")", "ZS3D2DFont2", 0, -25, owner == MySelf and COLOR_LBLUE or COLOR_GRAY, TEXT_ALIGN_CENTER)  end   self:Draw3DHealthBar(math.Clamp(self:GetObjectHealth() / self:GetMaxObjectHealth(), 0, 1), nil, 300)   cam.End3D2D() end 