include("shared.lua") include("cl_animations.lua")  ENT.ColorModulation = Color(1, 0.5, 0)  local function get_color_mod(self)     local lvl = self:GetInventoryItemLevel()     if lvl >= 1 and GAMEMODE.WeaponQualityColors[lvl] then         local col = GAMEMODE.WeaponQualityColors[lvl].Upg          return Color(col.r/255, col.g/255, col.b/255)     end      return self.ColorModulation end  function ENT:Think()  local itype = self:GetInventoryItemType()  if itype ~= self.LastInvItemType then  self.LastInvItemType = itype   self:RemoveModels()   local invdata = GAMEMODE.ZSInventoryItemData[itype]   if not invdata then  return  end   local droppedeles = invdata.DroppedEles   if invdata then     self.ShowBaseModel = not istable(droppedeles)    if istable(droppedeles) then  self.WElements = table.FullCopy(droppedeles)  self:CreateModels(self.WElements)  end   self.ColorModulation = get_color_mod(self)  self.PropWeapon = true  end  end end  function ENT:OnRemove()  self:RemoveModels() end 