ENT.Type = "anim" ENT.RenderGroup = RENDERGROUP_BOTH  ENT.SWEP = "weapon_zs_sensor" ENT.MaxHealth = 75  ENT.m_NoNailUnfreeze = true ENT.NoNails = true ENT.IgnoreBullets = true  ENT.CanPackUp = true ENT.PackUpTime = 1  ENT.AlwaysGhostable = true  ENT.Range = 500  function ENT:GetObjectHealth()  return self:GetDTFloat(3) end  function ENT:GetMaxObjectHealth()  return self:GetDTInt(1) end  function ENT:GetObjectOwner()  return self:GetDTEntity(1) end  function ENT:GetScanFilter()  local filter = team.GetPlayers(TEAM_HUMAN)  filter[#filter + 1] = self  filter = table.Add(filter, ents.FindByClass("prop_ffemitterfield"))  filter = table.Add(filter, ents.FindByClass("projectile_*"))  filter = table.Add(filter, ents.FindByClass("prop_hitbox_sensor"))   return filter end  local NextCache = 0 function ENT:GetCachedScanFilter()  if CurTime() < NextCache and self.CachedFilter then return self.CachedFilter end   self.CachedFilter = self:GetScanFilter()  NextCache = CurTime() + 1   return self.CachedFilter end 