ENT.Type = "anim" ENT.RenderGroup = RENDERGROUP_TRANSLUCENT  ENT.NoNails = true ENT.NoBlockExplosions = true ENT.IgnoreTraces = true ENT.IgnoreBullets = true ENT.IgnoreMelee = true  ENT.MaxSpeed = 360 ENT.Acceleration = 3600  ENT.DieTime = 5  function ENT:GetShootPos()  return self:LocalToWorld(Vector(0, 0, self:OBBMaxs().z - (4 + (self:GetOwner():KeyDown(IN_DUCK) and 4 or 0)))) end  function ENT:CheckGround()  local trace = {mask = MASK_SOLID, filter = function(ent) return ent:IsWorld() or ent:IsPhysicsProp() end, mins = Vector(-14, -14, 0), maxs = Vector(14, 14, 3)}  trace.start = self:GetPos()  trace.endpos = trace.start - Vector(0, 0, 3 + (self:GetOwner():KeyDown(IN_DUCK) and 0 or 36))  local tr = util.TraceHull(trace)   return tr end  function ENT:SetNextTeleport(t)  self:SetDTFloat(0, t) end  function ENT:GetNextTeleport()  return self:GetDTFloat(0) end  function ENT:GetThirdPersonCameraPos(origin, angles)     local tr = util.TraceHull({start = origin, endpos = origin + angles:Forward() * -36, mask = MASK_SHOT, filter = function(ent) return not (ent == self or ent:IsPlayer() or ent:IsProjectile()) end, mins = Vector(-8, -8, -8), maxs = Vector(8, 8, 8)})     return tr.HitPos + tr.HitNormal * 3 end 