include("shared.lua")  ENT.NextEmit = 0  function ENT:Initialize()  local owner = self:GetOwner()  if not owner:IsValid() then return true end  self.Mins = owner:OBBMins() * 1.02  self.Maxs = owner:OBBMaxs() * 1.02 end  function ENT:Draw()  if CurTime() < self.NextEmit then return end   local owner = self:GetOwner()  if owner:IsValid() then   self.NextEmit = UnPredictedCurTime() + 0.15  local pos = owner:GetPos()  local emitter = ParticleEmitter(pos)  emitter:SetNearClip(16, 24)   for i=1, 2 do  local fix_dim = math.random(3)  local rand_vec = Vector(0, 0, 0)             rand_vec.x = math.Rand(self.Mins.x, self.Maxs.x)             rand_vec.y = math.Rand(self.Mins.y, self.Maxs.y)             rand_vec.z = math.Rand(self.Mins.z, self.Maxs.z)   rand_vec[fix_dim] = math.random(2) == 1 and self.Mins[fix_dim] or self.Maxs[fix_dim]   pos = owner:LocalToWorld( rand_vec )   local particle = emitter:Add("particles/smokey", pos)  particle:SetVelocity(VectorRand():GetNormalized() * math.Rand(10, 20))  particle:SetAirResistance(200)  particle:SetDieTime(math.Rand(0.7, 0.8))  particle:SetStartAlpha(200)  particle:SetEndAlpha(0)  particle:SetStartSize(math.Rand(3, 5))  particle:SetEndSize(14)  particle:SetRoll(math.Rand(0, 360))  particle:SetRollDelta(math.Rand(-30, 30))  particle:SetColor(30, 200, 0)  particle:SetGravity(Vector(0, 0, 300))  end  emitter:Finish() emitter = nil collectgarbage("step", 64)  end end 