AddCSLuaFile()  ENT.Type = "anim" ENT.Base = "status__base"  ENT.Ephemeral = true  ENT.WoundBuildup = 0  AccessorFuncDT(ENT, "Duration", "Float", 0) AccessorFuncDT(ENT, "StartTime", "Float", 4)  function ENT:Initialize()  self.BaseClass.Initialize(self)   local enty = self  hook.Add("Move", tostring(enty), function(pl, move)  if not IsValid(enty) then return end  if pl ~= enty:GetOwner() then return end   if SERVER then  local owner = enty:GetOwner()   if owner:ActiveBarricadeGhosting() then  enty.WoundBuildup = enty.WoundBuildup + FrameTime() * 3  if enty.WoundBuildup >= 1 then  local wholewound = math.floor(enty.WoundBuildup)  owner:AddWounds(wholewound)  enty.WoundBuildup = enty.WoundBuildup - wholewound  end  end  end  end)   if SERVER then  self:EmitSound("ambient/machines/slicer3.wav", 70, 92)  end end  function ENT:OnRemove()  self.BaseClass.OnRemove(self)   hook.Remove("Move", tostring(self)) end  function ENT:PlayerSet()  self.LastPos = self:GetOwner():GetPos()  self:SetStartTime(CurTime()) end 