AddCSLuaFile()  ENT.Type = "anim" ENT.Base = "status__base"  ENT.Ephemeral = true  AccessorFuncDT(ENT, "Duration", "Float", 0) AccessorFuncDT(ENT, "StartTime", "Float", 4)  function ENT:Initialize()  self.BaseClass.Initialize(self)   if SERVER then  self:EmitSound("physics/glass/glass_impact_bullet"..math.random(4)..".wav", 70, 85)  end end  function ENT:PlayerSet()  self:SetStartTime(CurTime())   local owner = self:GetOwner()  if owner:IsValid() and owner.FrostDurationMul then  local newdur = self:GetDuration() * owner.FrostDurationMul  self.DieTime = CurTime() + newdur  self:SetDuration(newdur)          owner:ResetStaminaRegen()          self.EndOwner = owner  end end  function ENT:OnRemove()  self.BaseClass.OnRemove(self)      local owner = self.EndOwner     timer.Simple(0, function()         if not IsValid(owner) then return end          owner:ResetStaminaRegen()     end) end