AddCSLuaFile()  ENT.Type = "anim" ENT.Base = "status__base"  ENT.Ephemeral = true  AccessorFuncDT(ENT, "Duration", "Float", 0) AccessorFuncDT(ENT, "StartTime", "Float", 4)  function ENT:Initialize()  self.BaseClass.Initialize(self)      local enty = self  if SERVER then  self:EmitSound("physics/glass/glass_impact_bullet"..math.random(4)..".wav", 70, 85)   self:CreateSVHook(enty)  end end  function ENT:OnRemove()  self.BaseClass.OnRemove(self)   if SERVER then  self:RemoveSVHook(tostring(self))  end end  function ENT:PlayerSet()  self:SetStartTime(CurTime()) end