ENT.Type = "anim" ENT.Base = "status__base"  ENT.Ephemeral = true  AccessorFuncDT(ENT, "Duration", "Float", 0) AccessorFuncDT(ENT, "StartTime", "Float", 4)  function ENT:Initialize()  self.BaseClass.Initialize(self)      local enty = self  hook.Add("Move", tostring(enty), function(pl, move)         if not IsValid(enty) then return end         if pl ~= enty:GetOwner() then return end          local sloweffect = 1 - 0.4 * (pl.SlowEffTakenMul or 1)          move:SetMaxSpeed(move:GetMaxSpeed() * sloweffect)         move:SetMaxClientSpeed(move:GetMaxClientSpeed() * sloweffect)     end) end  function ENT:PlayerSet()  self:SetStartTime(CurTime()) end  function ENT:OnRemove()     self.BaseClass.OnRemove(self)      hook.Remove("Move", tostring(self)) end