include("shared.lua")  ENT.NextEmit = 0  local radius = 14 function ENT:Draw()  local owner = self:GetOwner()  if not self:ShouldDrawStatusEffect(owner) then return end   if CurTime() < self.NextEmit then return end  self.NextEmit = CurTime() + 0.2   local pos = owner:WorldSpaceCenter()  pos.z = pos.z + 48   local emitter = ParticleEmitter(pos)  emitter:SetNearClip(16, 24)   for i = 1, 9 do         local axis = Angle(0, i * (360/33), 0)         local offset = Vector(math.cos(axis.y) * math.Rand(1, radius), math.sin(axis.y) * math.Rand(1, radius))   particle = emitter:Add("sprites/light_glow02_add", pos + offset)  particle:SetDieTime(0.4)  particle:SetStartAlpha(230)  particle:SetEndAlpha(0)  particle:SetStartSize(11)  particle:SetEndSize(0)  particle:SetGravity(Vector(0, 0, -1))  particle:SetAirResistance(300)  particle:SetColor(255, 157, 30)         particle:SetVelocity(VectorRand() * 20)  end   emitter:Finish() emitter = nil collectgarbage("step", 64) end 