AddCSLuaFile()  ENT.Type = "anim" ENT.Base = "status__base"  ENT.LifeTime = 3  ENT.Ephemeral = true  function ENT:Initialize()  self.BaseClass.Initialize(self)   if CLIENT then         local enty = self  hook.Add("CreateMove", tostring(enty), function(cmd)             if not IsValid(enty) then return end             if MySelf ~= enty:GetOwner() then return end              local curtime = CurTime()             local frametime = FrameTime()             local power = enty:GetPower()              local ang = cmd:GetViewAngles()             ang.pitch = math.Clamp(ang.pitch + math.sin(curtime) * 40 * frametime * power, -89, 89)             ang.yaw = math.NormalizeAngle(ang.yaw + math.cos(curtime + enty.Seed) * 50 * frametime * power)              cmd:SetViewAngles(ang)         end)  hook.Add("RenderScreenspaceEffects", tostring(enty), function()             if not IsValid(enty) then return end          if MySelf ~= enty:GetOwner() then return end              local power = enty:GetPower()              DrawMotionBlur(0.1, power, 0.05)         end)   self.Seed = math.Rand(0, 10)  end   local parent = self:GetParent()  if parent:IsValid() and (SERVER or CLIENT and MySelf == parent) then  parent:SetDSP(35)  end   self.DieTime = CurTime() + self.LifeTime end  if SERVER then return end  function ENT:GetPower()  return math.Clamp(self.DieTime - CurTime(), 0, 1) end  function ENT:OnRemove()     self.BaseClass.OnRemove(self)      hook.Remove("CreateMove", tostring(self))     hook.Remove("RenderScreenspaceEffects", tostring(self)) end