include("shared.lua")  ENT.NextEmit = 0  function ENT:Draw()  local ent = self:GetOwner()  if not ent:IsValid() then return end   if CurTime() < self.NextEmit then return end  self.NextEmit = CurTime() + 0.1   local emitter = ParticleEmitter(self:GetPos())  emitter:SetNearClip(24, 32)   for i = 1, 6 do  local pos = self:GetPos()  pos.z = pos.z - math.random(5, 15)   local particle = emitter:Add("effects/fire_embers"..math.random(1, 3), pos + VectorRand():GetNormalized() * 9)  particle:SetDieTime(math.Rand(0.3, 0.4))  particle:SetStartSize(10)  particle:SetEndSize(1)  particle:SetStartAlpha(255)  particle:SetEndAlpha(0)  particle:SetVelocity(ent:GetVelocity() - Vector(0, 0, 60))  particle:SetRoll(math.Rand(0, 360))  particle:SetStartLength(10)  particle:SetEndLength(1)  particle:SetColor(30, 255, 160)  particle:SetRollDelta(math.Rand(-1.5, 1.5))  end   emitter:Finish() end 