include("shared.lua")  ENT.RenderGroup = RENDERGROUP_NONE  function ENT:Initialize()  self:DrawShadow(false)   self.AmbientSound = CreateSound(self, "npc/antlion_guard/confused1.wav")  self.AmbientSound:PlayEx(0.3, 110) end  function ENT:OnRemove()  self.AmbientSound:Stop() end  function ENT:Think()  local owner = self:GetOwner()  if owner.FeignDeath and owner.FeignDeath:IsValid() then  self.AmbientSound:Stop()  else  self.AmbientSound:PlayEx(0.3, 70 + math.sin(RealTime() * 3) * 10)  end end  function ENT:Draw() end 