include("shared.lua")  ENT.RenderGroup = RENDERGROUP_NONE ENT.NextEmit = 0  function ENT:Initialize()  self:DrawShadow(false)    end  function ENT:OnRemove()  self.AmbientSound:Stop() end  function ENT:Think()     if not self.AmbientSound then         self.AmbientSound = CreateSound(self, "vo/npc/vortigaunt/vortigese02.wav")     end   local owner = self:GetOwner()  if owner.FeignDeath and owner.FeignDeath:IsValid() then  self.AmbientSound:Stop()  else  self.AmbientSound:PlayEx(1, 55 + math.sin(RealTime() * 115) * 8 + math.sin(RealTime() * 0.6) * 10)  self.AmbientSound:SetDSP(7)  self.AmbientSound:SetSoundLevel(82)  end end  function ENT:Draw()  local owner = self:GetOwner()  if not self:ShouldDrawStatusEffect(owner) then return end   if owner:GetPos():DistToSqr(EyePos()) > 10000 then return end   local pos = owner:WorldSpaceCenter()   if CurTime() < self.NextEmit then return end  self.NextEmit = CurTime() + 0.2   local emitter = ParticleEmitter(pos)  emitter:SetNearClip(16, 24)   local dir = (VectorRand() * 20 + Vector(0, 0, 90)):GetNormal()   for i = 1, 10 do  local particle = emitter:Add("!sprite_bloodspray"..math.random(8), pos)  particle:SetVelocity(dir * 110)  particle:SetDieTime(math.Rand(0.7, 1.0))  particle:SetStartAlpha(80)  particle:SetEndAlpha(0)  particle:SetStartSize(math.Rand(6, 7))  particle:SetEndSize(5)  particle:SetRoll(math.Rand(0, 360))  particle:SetRollDelta(math.Rand(-5, 5))  particle:SetGravity(Vector(0, 0, -165))  particle:SetBounce(0.45)  particle:SetAirResistance(12)  particle:SetColor(33, 33, 33)  end   emitter:Finish() emitter = nil collectgarbage("step", 64) end 