include("shared.lua")  ENT.RenderGroup = RENDERGROUP_NONE  ENT.NextEmit = 0  function ENT:Initialize()  self:DrawShadow(false) end  function ENT:OnRemove()  self.AmbientSound:Stop()  self.AmbientSoundChainsaw:Stop() end  function ENT:Think()     if not self.AmbientSound then         self.AmbientSound = CreateSound(self, "npc/zombie_poison/pz_breathe_loop2.wav")         self.AmbientSound:PlayEx(1, 20)                  self.AmbientSoundChainsaw = CreateSound(self, "vehicles/digger_grinder_loop1.wav")         self.AmbientSoundChainsaw:PlayEx(0.5, 60)     end end  function ENT:Draw()  if CurTime() < self.NextEmit then return end    self.NextEmit = CurTime() + 0.1    local owner = self:GetOwner()  if owner:IsValid() then  local pos, ang = owner:GetBonePosition( 29 )  if pos and ang then  local emitter = ParticleEmitter(pos)  emitter:SetNearClip(24, 32)   local particle = emitter:Add("particles/smokey", pos + VectorRand()*2)  particle:SetVelocity(( ang:Up() - ang:Forward() ) * math.random( 40, 60 ) )  particle:SetDieTime(math.Rand(0.4, 0.8))  particle:SetStartSize(5)  particle:SetEndSize(23)  particle:SetStartAlpha(100)  particle:SetEndAlpha(0)  particle:SetRoll(math.Rand(0, 360))  particle:SetColor(70, 70, 70)  particle:SetAirResistance(15)    emitter:Finish() emitter = nil collectgarbage("step", 64)    if math.random( 25 ) == 25 then  local e = EffectData()  e:SetOrigin( pos )  e:SetNormal( ( ang:Up() - ang:Forward() + VectorRand() * 0.1 ):GetNormal() )  e:SetScale( 0.2 )  e:SetMagnitude( 0.2 )  util.Effect( "ManhackSparks", e, nil, nil )  end  end  end end 