include("shared.lua")  ENT.RenderGroup = RENDERGROUP_NONE  function ENT:Initialize()  self:DrawShadow(false)   self.AmbientSound = CreateSound(self, "npc/zombie_poison/pz_breathe_loop2.wav")  self.AmbientSound:PlayEx(0.55, 110) end  function ENT:OnRemove()  self.AmbientSound:Stop() end  function ENT:Think()  local owner = self:GetOwner()  if owner.FeignDeath and owner.FeignDeath:IsValid() then  self.AmbientSound:Stop()  else  self.AmbientSound:PlayEx(1, 40 + math.sin(RealTime() * 200) * 5)  end end  function ENT:Draw() end 