include("shared.lua")  ENT.NextEmit = 0  function ENT:Initialize()  self:DrawShadow(false)  self:SetRenderBounds(Vector(-40, -40, -18), Vector(40, 40, 80))   local owner = self:GetOwner()  if owner:IsValid() then  owner[self:GetClass()] = self  end   self:OnInitialize()   self.AmbientSound = CreateSound(self, "items/medshotno1.wav")  self.Created = CurTime() end  function ENT:Think()  local owner = self:GetOwner()  if owner:IsValid() then self:SetPos(owner:EyePos()) end   if owner == MySelf then  self.AmbientSound:PlayEx(1, 20 + math.min(1, CurTime() - self.Created) * 90)  end   self:NextThink(CurTime())  return true end  function ENT:Draw()  local owner = self:GetOwner()  if not self:ShouldDrawStatusEffect(owner) then return end   if CurTime() < self.NextEmit then return end  self.NextEmit = CurTime() + 0.3   local pos = owner:WorldSpaceCenter()   local emitter = ParticleEmitter(pos)  emitter:SetNearClip(16, 24)   for i = 1, 2 do  particle = emitter:Add("sprites/light_glow02_add", pos + VectorRand() * 12)  particle:SetDieTime(math.Rand(1.1, 1.2))  particle:SetStartAlpha(230)  particle:SetEndAlpha(0)  particle:SetStartSize(3)  particle:SetEndSize(6)  particle:SetGravity(Vector(0, 0, 75))  particle:SetAirResistance(300)  particle:SetStartLength(1)  particle:SetEndLength(16)  particle:SetColor(255, 30, 30)  end   emitter:Finish() emitter = nil collectgarbage("step", 64) end 