ENT.Type = "anim" ENT.Base = "status__base"  ENT.Ephemeral = true  function ENT:Initialize()  self.BaseClass.Initialize(self)      local enty = self  hook.Add("Move", tostring(enty), function(pl, move)         if not IsValid(enty) then return end         if pl ~= enty:GetOwner() then return end          move:SetMaxSpeed(move:GetMaxSpeed() + 20)      move:SetMaxClientSpeed(move:GetMaxSpeed())     end) end  function ENT:OnRemove()     self.BaseClass.OnRemove(self)      hook.Remove("Move", tostring(self)) end