include("shared.lua")  ENT.NextEmit = 0  function ENT:Draw()  local owner = self:GetOwner()  if not self:ShouldDrawStatusEffect(owner) then return end       local pos = owner:WorldSpaceCenter()   if CurTime() < self.NextEmit then return end  self.NextEmit = CurTime() + 0.25   local emitter = ParticleEmitter(pos)  emitter:SetNearClip(16, 24)   local dir = (VectorRand() * 50 + Vector(0, 0, 60)):GetNormal()   for i = 1, 2 do  pos.z = pos.z + math.random(-3, 3)         local particle = emitter:Add("particle/smokestack", pos)         particle:SetVelocity(owner:GetVelocity() * 0.8)         particle:SetDieTime(math.Rand(0.5, 0.6))         particle:SetStartAlpha(130)         particle:SetEndAlpha(0)         particle:SetStartSize(5)         particle:SetEndSize(math.Rand(24,30))         particle:SetRoll(math.Rand(0, 360))         particle:SetRollDelta(math.Rand(-5, 5))         particle:SetGravity(Vector(0, 0, 90))         particle:SetBounce(0.45)         particle:SetAirResistance(12)         particle:SetColor(155, 255, 255)  end   emitter:Finish() emitter = nil collectgarbage("step", 64) end 