ENT.Type = "anim" ENT.Base = "status__base"  ENT.Ephemeral = true  function ENT:Initialize()  self.BaseClass.Initialize(self)      local enty = self  if SERVER then  self:CreateSVHook(enty)   self:SetDTInt(1, 0)  end       hook.Add("Move", tostring(enty), function(pl, move)         if not IsValid(enty) then return end         if pl ~= enty:GetOwner() then return end          move:SetMaxSpeed(move:GetMaxSpeed() + 25)         move:SetMaxClientSpeed(move:GetMaxSpeed())     end) end  function ENT:OnRemove()     self.BaseClass.OnRemove(self)      local enty = self     hook.Remove("Move", tostring(enty))      if SERVER then  self:RemoveSVHook(enty)  end end