include("shared.lua")  ENT.NextEmit = 0  function ENT:Draw()     local owner = self:GetOwner()     if not self:ShouldDrawStatusEffect(owner) then return end          local pos = owner:WorldSpaceCenter()      if CurTime() < self.NextEmit then return end     self.NextEmit = CurTime() + 0.25      local emitter = ParticleEmitter(pos)     emitter:SetNearClip(16, 24)      for i = 1, 3 do         local pos = self:GetPos()         pos.z = pos.z - math.random(60, 63)          local particle = emitter:Add("effects/fire_embers"..math.random(1, 3), pos + VectorRand():GetNormalized() * 17)         particle:SetDieTime(math.Rand(0.3, 0.4))         particle:SetStartSize(1)         particle:SetEndSize(6)         particle:SetStartAlpha(255)         particle:SetEndAlpha(0)         particle:SetVelocity(Vector(0, 0, 1))         particle:SetAirResistance(32)         particle:SetRoll(math.Rand(0, 360))         particle:SetStartLength(5)         particle:SetEndLength(95)         particle:SetColor(161, 240, 130)         particle:SetRollDelta(math.Rand(-1.5, 1.5))     end      emitter:Finish() emitter = nil collectgarbage("step", 64) end 