include("shared.lua")  ENT.NextEmit = 0  function ENT:Draw()  local owner = self:GetOwner()  if not self:ShouldDrawStatusEffect(owner) then return end       local pos = owner:WorldSpaceCenter()   if CurTime() < self.NextEmit then return end  self.NextEmit = CurTime() + 0.1   local emitter = ParticleEmitter(pos)  emitter:SetNearClip(16, 24)   local dir = (VectorRand() * 10 + Vector(0, 0, 50)):GetNormal()   for i = 1, 9 do  pos.z = pos.z + math.random(-6, 0)  local particle = emitter:Add("!sprite_bloodspray"..math.random(8), pos + VectorRand() * 8)  particle:SetVelocity(dir * 90)  particle:SetDieTime(math.Rand(0.6, 0.9))  particle:SetStartAlpha(250)  particle:SetEndAlpha(0)  particle:SetStartSize(math.Rand(15, 16))  particle:SetEndSize(0)  particle:SetRoll(math.Rand(0, 360))  particle:SetRollDelta(math.Rand(-1, 1))  particle:SetGravity(Vector(0, 0, -15))  particle:SetAirResistance(1)  particle:SetColor(10, 30, 30)  end   emitter:Finish() emitter = nil collectgarbage("step", 64) end