include("shared.lua")  ENT.NextEmit = 0  function ENT:Draw()  local owner = self:GetOwner()  if not self:ShouldDrawStatusEffect(owner) then return end       local pos = owner:WorldSpaceCenter()   if CurTime() < self.NextEmit then return end  self.NextEmit = CurTime() + 0.25   local emitter = ParticleEmitter(pos)  emitter:SetNearClip(16, 24)   local dir = (VectorRand() * 50 + Vector(0, 0, 60)):GetNormal()   for i = 1, 10 do  pos.z = pos.z + math.random(-3, 3)  local particle = emitter:Add("!sprite_bloodspray"..math.random(8), pos + VectorRand() * 5)  particle:SetVelocity(dir * 50)  particle:SetDieTime(math.Rand(0.6, 0.9))  particle:SetStartAlpha(150)  particle:SetEndAlpha(0)  particle:SetStartSize(math.Rand(15, 16))  particle:SetEndSize(1)  particle:SetRoll(math.Rand(0, 360))  particle:SetRollDelta(math.Rand(-5, 5))  particle:SetGravity(Vector(0, 0, -45))  particle:SetAirResistance(200)  particle:SetColor(255, 255, 30)  end   emitter:Finish() emitter = nil collectgarbage("step", 64) end 