include("shared.lua")  ENT.RenderGroup = RENDERGROUP_NONE  function ENT:Initialize()  self:DrawShadow(false)   self.AmbientSound = CreateSound(self, "zombiesurvival/nightmare_ambiance.ogg")  self.AmbientSound:PlayEx(0.8, 100)   self.DarkHandSound = CreateSound(self, "hl1/ambience/labdrone2.wav") end  function ENT:OnRemove()  self.AmbientSound:Stop() end  function ENT:Think()  local owner = self:GetOwner()  local activ = owner:GetActiveWeapon()   if IsValid(activ) and activ.GetDarkBlock and activ:GetDarkBlock() then  self.DarkHandSound:PlayEx(1, 70)  else  self.DarkHandSound:Stop()  end end  function ENT:Draw() end 