include("shared.lua")  ENT.RenderGroup = RENDERGROUP_TRANSLUCENT ENT.NextEmit = 0  function ENT:Initialize()  self:DrawShadow(false) end  function ENT:Draw()  local owner = self:GetOwner()  if not self:ShouldDrawStatusEffect(owner) then return end   local pos = owner:WorldSpaceCenter()     pos.z = pos.z + 5   if CurTime() < self.NextEmit then return end  self.NextEmit = CurTime() + 0.25   local emitter = ParticleEmitter(pos)  emitter:SetNearClip(16, 24)      for i = 1, 5 do         local particle = emitter:Add("!sprite_bloodspray"..math.random(8), pos + VectorRand() * 9)         particle:SetDieTime(math.Rand(0.6, 0.7))         particle:SetStartAlpha(240)         particle:SetEndAlpha(0)         particle:SetStartSize(math.Rand(3, 4))         particle:SetEndSize(4)         particle:SetRoll(math.Rand(0, 360))         particle:SetRollDelta(math.Rand(-5, 5))         particle:SetGravity(Vector(0, 0, -115))         particle:SetAirResistance(12)         particle:SetStartLength(1)         particle:SetEndLength(45)         particle:SetColor(230, 0, 0)     end   emitter:Finish() emitter = nil collectgarbage("step", 64) end 