ENT.Type = "anim" ENT.Base = "status__base"  AccessorFuncDT(ENT, "Duration", "Float", 0) AccessorFuncDT(ENT, "StartTime", "Float", 4)  local colModDimVision = {  ["$pp_colour_colour"] = 1,  ["$pp_colour_brightness"] = 0,  ["$pp_colour_contrast"] = 1,  ["$pp_colour_mulr"] = 0,  ["$pp_colour_mulg"] = 0,  ["$pp_colour_mulb"] = 0,  ["$pp_colour_addr"] = 0,  ["$pp_colour_addg"] = 0,  ["$pp_colour_addb"] = 0 }  function ENT:PlayerSet()  self:SetStartTime(CurTime()) end  function ENT:Initialize()  self.BaseClass.Initialize(self)      local enty = self  if SERVER then  self:CreateSVHook(enty)  self:EmitSound("^npc/strider/strider_step1.wav", 65, 120)  self:SetDTInt(1, 0)  end      if CLIENT then         hook.Add("RenderScreenspaceEffects", tostring(enty), function()             if not IsValid(enty) then return end             if MySelf ~= enty:GetOwner() then return end              colModDimVision["$pp_colour_brightness"] = self:GetPower() * 0.1             DrawColorModify(colModDimVision)         end)     end end  function ENT:OnRemove()  self.BaseClass.OnRemove(self)   if SERVER then  self:RemoveSVHook(tostring(self))  else         hook.Remove("RenderScreenspaceEffects", tostring(self))     end end