include("shared.lua")  ENT.RenderGroup = RENDERGROUP_TRANSLUCENT ENT.NextEmit = 0 ENT.ParticleMaterial = "sprites/glow04_noz"  function ENT:Initialize()     self:DrawShadow(false)     self.AmbientSound = CreateSound(self, "npc/combine_gunship/gunship_engine_loop3.wav")     self.AmbientSound:PlayEx(0.55, 90) end  function ENT:OnRemove()     self.AmbientSound:Stop() end  function ENT:Draw()     local owner = self:GetOwner()     if not self:ShouldDrawStatusEffect(owner) then return end          local pos = owner:WorldSpaceCenter()      if CurTime() < self.NextEmit then return end     self.NextEmit = CurTime() + 0.25      local emitter = ParticleEmitter(pos)     emitter:SetNearClip(16, 24)      local particle = emitter:Add(self.ParticleMaterial, pos)         particle:SetDieTime(2)         particle:SetVelocity(Vector(math.random(-20,20), math.random(-20,20), -4))         particle:SetAirResistance(64)         particle:SetStartAlpha(120)         particle:SetEndAlpha(0)         particle:SetStartSize(math.Rand(6, 8))         particle:SetEndSize(12)         particle:SetRoll(math.Rand(0, 360))         particle:SetRollDelta(math.Rand(-6, 6))         particle:SetGravity(Vector(0, 0, -165))         particle:SetBounce(0.45)         particle:SetCollide(true)     if math.random(4) ~= 1 then         self:SetParticleColor(particle)     end          emitter:Finish() emitter = nil collectgarbage("step", 64) end  function ENT:SetParticleColor(particle)     particle:SetColor(0, 180, 255) end