SWEP.Base = "weapon_zs_zombietorso"  SWEP.PrintName = "Abyss Lurker"  SWEP.MeleeDelay = 0.45 SWEP.MeleeDamage = 25  SWEP.Secondary.Delay = 3  function SWEP:PlayHitSound()  self:EmitSound("NPC_FastZombie.AttackHit", nil, nil, nil, CHAN_AUTO) end  function SWEP:PlayMissSound()  self:EmitSound("NPC_FastZombie.AttackMiss", nil, nil, nil, CHAN_AUTO) end  function SWEP:PlayAttackSound()  self:EmitSound("npc/zombie_poison/pz_warn"..math.random(2)..".wav", 70, math.random(170, 180), nil, CHAN_AUTO)  self:GetOwner():EmitSound("npc/headcrab_poison/ph_warning"..math.random(3)..".wav", 70, math.random(70, 80), 0.5, CHAN_AUTO) end  function SWEP:PlayIdleSound()  self:GetOwner():EmitSound("npc/antlion/idle"..math.random(5)..".wav", 70, math.random(60, 66)) end  function SWEP:PlayAlertSound()  self:GetOwner():EmitSound("npc/stalker/breathing3.wav", 70, math.random(80, 90)) end  local function DoFleshThrow(pl, wep)  if pl:IsValid() and pl:Alive() and wep:IsValid() then  pl:ResetSpeed()  pl.LastRangedAttack = CurTime()   if SERVER then  local startpos = pl:GetShootPos()  local aimang = pl:EyeAngles()  local ang   ang = Angle(aimang.p, aimang.y, aimang.r)   local ent = ents.Create("projectile_ghoulfleshabyss")  if ent:IsValid() then  ent:SetPos(startpos)  ent:SetOwner(pl)  ent:Spawn()   local phys = ent:GetPhysicsObject()  if phys:IsValid() then  phys:SetVelocityInstantaneous(ang:Forward() * 660)  end  end   pl:EmitSound(string.format("physics/body/body_medium_break%d.wav", math.random(2, 4)), 72, math.Rand(85, 95))  end  end end  function SWEP:SecondaryAttack()  local owner = self:GetOwner()  if CurTime() < self:GetNextPrimaryFire() or CurTime() < self:GetNextSecondaryFire() then return end   self:SetNextSecondaryFire(CurTime() + self.Secondary.Delay)  self:SetNextPrimaryFire(CurTime() + self.Primary.Delay)   self:GetOwner():DoZombieEvent()  self:EmitSound("npc/fast_zombie/leap1.wav", 74, math.Rand(110, 130))  self:EmitSound(string.format("physics/body/body_medium_break%d.wav", math.random(2, 4)), 72, math.Rand(85, 95))  self:SendWeaponAnim(ACT_VM_HITCENTER)  self.IdleAnimation = CurTime() + self:SequenceDuration()   timer.Simple(0.45, function() DoFleshThrow(owner, self) end) end