AddCSLuaFile()  SWEP.PrintName = "Adrenaline" SWEP.Description = "When injected, gives you the ability to take a few more hits, blood armour and speed."  SWEP.Base = "weapon_zs_baseconsumable"  SWEP.MaxStock = 10  SWEP.Weight = 10  function SWEP:ItemUse()     local owner = self:GetOwner()      if IsValid(owner) then         owner:EmitSound("items/smallmedkit1.wav", 150, math.random(50, 55))          for i = 1, 3 do             owner:GiveStack("shield")         end         local boost = owner:GiveStatus("buff_adrenaline", 8)         if boost and boost:IsValid() then             boost:SetSpeed(50)         end         owner:SetBloodArmor(math.min(owner:GetBloodArmor() + 25 * owner.BloodarmorGainMul, owner:GetBloodArmorMax()))     end end